   117â†’        if reviewers_dir is not None:
   118â†’            self.reviewers_dir = Path(reviewers_dir)
   119â†’        else:
   120â†’            project_root = Path(__file__).resolve().parent.parent.parent.parent
   121â†’            self.reviewers_dir = project_root / "reviewers"
   122â†’
   123â†’    def load_all(self, diff_files: list[str] | None = None) -> list[ReviewerConfig]:
   124â†’        """
   125â†’        Load all 5 reviewers with relevance gate based on diff files.
   126â†’
   127â†’        Args:
   128â†’            diff_files: List of file paths in the diff. Used to determine
   129â†’                        relevance. If None, all reviewers are marked relevant.
   130â†’
   131â†’        Returns:
   132â†’            List of 5 ReviewerConfig objects (one per reviewer).
   133â†’
   134â†’        Raises:
   135â†’            FileNotFoundError: If reviewers_dir does not exist.
   136â†’        """
   137â†’        if not self.reviewers_dir.exists():
   138â†’            raise FileNotFoundError(f"Reviewers directory not found: {self.reviewers_dir}")
   139â†’
   140â†’        configs: list[ReviewerConfig] = []
   141â†’        for reviewer_id in REVIEWER_IDS:
   142â†’            config = self.load_one(reviewer_id, diff_files=diff_files)
   143â†’            configs.append(config)
   144â†’
   145â†’        return configs
   146â†’
   147â†’    def load_one(
   148â†’        self,
   149â†’        reviewer_id: str,
   150â†’        diff_files: list[str] | None = None,
   151â†’    ) -> ReviewerConfig:
   152â†’        """
   153â†’        Load a single reviewer by ID.
   154â†’
   155â†’        Args:
   156â†’            reviewer_id: One of the REVIEWER_IDS.
   157â†’            diff_files: Optional list of diff file paths for relevance check.
   158â†’
   159â†’        Returns:
   160â†’            ReviewerConfig for the requested reviewer.
   161â†’
   162â†’        Raises:
   163â†’            ValueError: If reviewer_id is not in REVIEWER_IDS.
   164â†’            FileNotFoundError: If PROMPT.md is missing for the reviewer.
   165â†’        """
   166â†’        if reviewer_id not in REVIEWER_IDS:
   167â†’            raise ValueError(f"Unknown reviewer '{reviewer_id}'. Valid reviewers: {REVIEWER_IDS}")
   168â†’
   169â†’        reviewer_dir = self.reviewers_dir / reviewer_id
   170â†’
   171â†’        prompt_path = reviewer_dir / "PROMPT.md"
   172â†’        if not prompt_path.exists():
   173â†’            raise FileNotFoundError(
   174â†’                f"PROMPT.md not found for reviewer '{reviewer_id}': {prompt_path}"
   175â†’            )
   176â†’        prompt = prompt_path.read_text(encoding="utf-8")
   177â†’
   178â†’        knowledge_path = reviewer_dir / "KNOWLEDGE.md"
   179â†’        knowledge = ""
   180â†’        if knowledge_path.exists():
   181â†’            knowledge = knowledge_path.read_text(encoding="utf-8")
   182â†’
   183â†’        temperature = self._parse_temperature(prompt, reviewer_id)
   184â†’
   185â†’        if diff_files is not None:
   186â†’            relevant = self._check_relevance(reviewer_id, diff_files)
   187â†’        else:
   188â†’            relevant = True
   189â†’
   190â†’        return ReviewerConfig(
   191â†’            id=reviewer_id,
   192â†’            prompt=prompt,
   193â†’            knowledge=knowledge,
   194â†’            temperature=temperature,
   195â†’            relevant=relevant,
   196â†’        )
