   137â†’        if not self.reviewers_dir.exists():
   138â†’            raise FileNotFoundError(f"Reviewers directory not found: {self.reviewers_dir}")
   139â†’
   140â†’        configs: list[ReviewerConfig] = []
   141â†’        for reviewer_id in REVIEWER_IDS:
   142â†’            config = self.load_one(reviewer_id, diff_files=diff_files)
