func processFiles(paths []string) error {
	for _, p := range paths {
		if err := processOne(p); err != nil {
			return fmt.Errorf("processing %s: %w", p, err)
		}
	}
	return nil
}

func processOne(path string) error {
	f, err := os.Open(path)
	if err != nil {
		return err
	}
	defer f.Close()
	return nil
}
