import logging

logger = logging.getLogger(__name__)

def fetch_with_retry(fetch_fn, retries: int = 3):
    for attempt in range(retries):
        try:
            return fetch_fn()
        except Exception as exc:
            logger.warning('Attempt %d failed: %s', attempt + 1, exc)
            if attempt == retries - 1:
                raise
    return None
