class Router {
  private routes: Map<string, () => void>;

  constructor() {
    this.routes = new Map();
  }

  // Non-null assertion is safe: we guard with has() immediately before
  navigate(path: string): void {
    if (this.routes.has(path)) {
      this.routes.get(path)!();
    }
  }
}
